import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
//
const Todo = props => (
    <tr>
        <td>{props.todo.todo_description}</td>
        <td>{props.todo.todo_responsible}</td>
        <td>{props.todo.todo_priority}</td>
        <td>
            <Link to={"/edit/"+props.todo._id}>Edit</Link>
            &nbsp;&nbsp;&nbsp;
            // <Link to={"/delete/"+props.todo._id}>Delete</Link>
            <button onClick={this.onClick}>Delete</button>
        </td>
    </tr>
)
export default class TodosList extends Component {
    constructor(props) {
        super(props);
        this.state = {todos: []};
    }
    componentDidMount() {
        axios.get('/todos/')
            .then(response => {
                this.setState({ todos: response.data });
            })
            .catch(function (error){
                console.log(error);
            })
    }
    //
    //Handle of onSubmit event in the form
    onClick(e){
        //we use preventDefault() method to ensure the default HTML form submit
        //behavior is prevented during test because the backend is not completed yet.
        e.preventDefault();
        //
        axios.post('/todos/delete/'+this.props.match.params.id)
        .then(res => console.log(res.data));
        //redirects user back to the todo list page
        this.props.history.push('/');
        //Refresh page
        window.location.reload();
        return false;
    }
    todoList() {
        return this.state.todos.map((currentTodo, i) => {
            return <Todo todo={currentTodo} key={i} />;
        })
    }
    //
    render() {
        return (
            <div>
                <h3>Todos List</h3>
                <table className="table table-striped" style={{ marginTop: 20 }} >
                    <thead>
                        <tr>
                            <th>Description</th>
                            <th>Responsible</th>
                            <th>Priority</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.todoList() }
                    </tbody>
                </table>
            </div>
        )
    }
}
